<template>
	<div class="confirm-btn__container">
		<edit-btn @click="showForm=true"></edit-btn>
		<button class="confirm-btn"
		        type="button"
		        :style="{'background-image': (activityConfig.game.confirmUrl!='' ? 'url('+activityConfig.game.confirmUrl+')' : 1)}">确定</button>
		<modal :show.sync="showForm"
		       size="md">
			<div class="modal-panel">
				<div class="title">编辑图片</div>
				<div class="close"
				     @click="showForm=false">X</div>
				<upload-image :img-url.sync="activityConfig.game.confirmUrl"
				              :config="uploadConfig"
				              :default-image="defaultImg">
				</upload-image>
			</div>
		</modal>
	</div>
</template>
<script>
import editBtn from '../../../../../components/edit-btn.vue'
import uploadImage from '../../../../../../components/web/uploadImage/uploadImage.vue'
import modal from '../../../../../../components/bootstrap/Modal.vue'
export default {
	components: {
		editBtn,
		uploadImage,
		modal
	},
	data() {
		var activityConfig = window.store.activityConfig
		return {
			activityConfig,
			showForm: false,
			uploadConfig: {
				width: "100%", // 宽度
				height: "200px",
				onlyFile: true,
				fileSize: 5120000, // 上传文件的大小
				multiple: false, // 是否可以多个文件上传
				dragDrop: false, // 是否可以拖动上传文件
				tailor: true, // 是否可以裁剪图片
				del: true, // 是否可以删除文件
				finishDel: true, // 是否在上传文件完成后删除预览
			},
			defaultImg: 'data:image/png;base64,'
		}
	},
	ready() {
	},
	methods: {

	},
	watch: {

	}
}
</script>
<style lang="scss">
$ratio: 750/320;
@import './common.scss';
</style>